INSERT INTO Peliculas (idPelicula, Titulo, Directores, Actores, Duracion, Sinopsis, UrlTrailer)
VALUES ('argylle', 'Argylle', 'Matthew Vaughn', 'Henry Cavill, Bryce Dallas Howard, Sam Rockwell, Bryan Cranston, Catherine O’Hara, Dua Lipa, Ariana DeBose, John Cena, Samuel L. Jackson', 139, 'Cuando las tramas de sus libros empiezan a parecerse demasiado a las actividades de un siniestro sindicato clandestino, la introvertida autora de novelas de espías Elly Conway y su gato se ven inmersos en el verdadero mundo del espionaje…, donde nada ni nadie es lo que parece.', 'https://www.youtube-nocookie.com/embed/7mgu9mNZ8Hk?si=JwqMsRSDDgNEgewd');

INSERT INTO Peliculas (idPelicula, Titulo, Directores, Actores, Duracion, Sinopsis, UrlTrailer)
VALUES ('wicked', 'Wicked: Part I', 'Jon M. Chu', 'Cynthia Erivo, Ariana Grande, Michelle Yeoh, Jeff Goldblum, Jonathan Bailey, Ethan Slater, Marissa Bode, Bowen Yang, Bronwyn James, Keala Settle', 165, 'Una amistad improbable surge entre Elphaba y Glinda, dos brujas enfrentadas en la mágica tierra de Oz. Elphaba es injustamente catalogada como una bruja malvada mientras intenta exponer al farsante y corrupto Mago que gobierna la tierra.', 'https://www.youtube-nocookie.com/embed/F1dvX9Vs0ns?si=T2O1ceF16vLVLArM');

INSERT INTO Peliculas (idPelicula, Titulo, Directores, Actores, Duracion, Sinopsis, UrlTrailer)
VALUES ('wonka', 'Wonka', 'Paul King', 'Timothée Chalamet, Calah Lane, Keegan-Michael Key, Paterson Joseph, Matt Lucas, Mathew Baynton, Sally Hawkins, Rowan Atkinson, Jim Carter, Olivia Colman, Hugh Grant', 116, 'Wonka es una película de fantasía musical de 2023 dirigida por Paul King, quien coescribió el guion con Simon Farnaby basado en una historia de King. Cuenta la historia de origen de Willy Wonka, un personaje de la novela de 1964 Charlie y la fábrica de chocolate de Roald Dahl, que representa sus primeros días como chocolatero.', 'https://www.youtube-nocookie.com/embed/otNh9bTjXWg?si=m7QWpRY7we67Ftrp');

INSERT INTO Peliculas (idPelicula, Titulo, Directores, Actores, Duracion, Sinopsis, UrlTrailer)
VALUES ('madame_web', 'Madame Web', 'S. J. Clarkson', 'Dakota Johnson, Sydney Sweeney, Isabela Merced, Celeste O´Connor, Tahar Rahim, Mike Epps, Emma Roberts, Adam Scott', 116, 'Madame Web es una película de superhéroes estadounidense de 2024 que presenta al personaje de cómics de Marvel del mismo nombre. Producida por Columbia Pictures y Di Bonaventura Pictures en asociación con Marvel Entertainment y TSG Entertainment, y distribuida por Sony Pictures Releasing, es la cuarta película en el Universo Spider-Man de Sony (SSU).', 'https://www.youtube-nocookie.com/embed/s_76M4c4LTo?si=m6w7eGTxBH1i_yEm');

INSERT INTO Peliculas (idPelicula, Titulo, Directores, Actores, Duracion, Sinopsis, UrlTrailer)
VALUES ('dune', 'Dune', 'Denis Villeneuve', 'Timothée Chalamet, Rebecca Ferguson, Oscar Isaac, Josh Brolin, Stellan Skarsgård, Dave Bautista, Stephen McKinley Henderson, Zendaya, Chang Chen, Sharon Duncan-Brewster, Charlotte Rampling, Jason Momoa, Javier Bardem', 155, 'Dune es una película de ciencia ficción épica estadounidense de 2021 dirigida y coproducida por Denis Villeneuve, quien coescribió el guion con Jon Spaihts y Eric Roth. Es la primera de una adaptación en dos partes de la novela de 1965 del mismo nombre de Frank Herbert.', 'https://www.youtube-nocookie.com/embed/n9xhJrPXop4?si=r4AsnaS5Kr3ER-0d');

INSERT INTO Peliculas (idPelicula, Titulo, Directores, Actores, Duracion, Sinopsis, UrlTrailer)
VALUES ('kung_fu_panda_4', 'Kung Fu Panda 4', 'Mike Mitchell', 'Jack Black, Awkwafina, Bryan Cranston, James Hong, Ian McShane, Ke Huy Quan, Dustin Hoffman, Viola Davis', 94, 'Kung Fu Panda 4 es una película de comedia de artes marciales animada estadounidense de 2024 producida por DreamWorks Animation y distribuida por Universal Pictures. Es la cuarta entrega de la franquicia Kung Fu Panda y la secuela de Kung Fu Panda 3 (2016).', 'https://www.youtube-nocookie.com/embed/_inKs4eeHiI?si=jj8LRu3iGAFs9ixj');

INSERT INTO Peliculas (idPelicula, Titulo, Directores, Actores, Duracion, Sinopsis, UrlTrailer)
VALUES ('one_love', 'One Love', 'Reinaldo Marcus Green', 'Kingsley Ben-Adir, Lashana Lynch, James Norton, Tosin Cole, Umi Myers, Anthony Welsh, Don Taylor, Nia Ashi, Teen Rita Marley, Quan-Dajai Henriques, Teen Bob Marley, Kait Tenison, Bene Gesserit Sister', 104, 'Bob Marley: One Love celebra la vida y la música de un ícono que inspiró a generaciones a través de su mensaje de amor y unidad. Por primera vez en la gran pantalla, descubre la poderosa historia de superación de Bob y el viaje detrás de su música revolucionaria.', 'https://www.youtube-nocookie.com/embed/ajw425Kuvtw?si=Ordk2lArJ359PQpF');

INSERT INTO Peliculas (idPelicula, Titulo, Directores, Actores, Duracion, Sinopsis, UrlTrailer)
VALUES ('dune2', 'Dune: Part II', 'Denis Villeneuve', 'Timothée Chalamet, Rebecca Ferguson, Josh Brolin, Stellan Skarsgård, Dave Bautista, Zendaya, Charlotte Rampling, Javier Bardem, Austin Butler, Florence Pugh, Christopher Walken, Léa Seydoux, Souheila Yacoub', 165, 'Dune: Part Two es una película de ciencia ficción épica estadounidense de 2024 dirigida y coproducida por Denis Villeneuve, quien coescribió el guion con Jon Spaihts. La secuela de Dune (2021), es la segunda de una adaptación en dos partes de la novela de 1965 Dune de Frank Herbert.', 'https://www.youtube-nocookie.com/embed/Way9Dexny3w?si=43_adQN2ZFaTawz6');

